yt-dlp --match-filter !is_live --force-ipv4 --cookies 'cookies.txt' --download-archive "archive.log" -i --add-metadata --write-info-json --sub-langs all,live_chat --embed-subs --embed-thumbnail -o '%(title)s - %(id)s.%(ext)s' "$1" --exec "bash upload.sh $2 %(filepath)q %(__infojson_filename)q %(subtitles.live_chat.-1.filepath)q"
